import React, { useState, useEffect } from 'react';

export default function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // تشغيل مكتبة Pi عند فتح التطبيق
    const script = document.createElement('script');
    script.src = "https://sdk.minepi.com/pi-sdk.js";
    script.async = true;
    script.onload = () => { window.Pi.init({ version: "2.0", sandbox: true }); };
    document.head.appendChild(script);
  }, []);

  const login = async () => {
    try {
      // هذا الأمر هو ما يجعل الزر يستجيب ويفتح نافذة Pi
      const auth = await window.Pi.authenticate(['username', 'payments'], (p) => {});
      setUser(auth.user);
    } catch (e) { alert("يجب فتح الرابط من داخل Pi Browser فقط!"); }
  };

  return (
    <div style={{backgroundColor:'#0b141a', height:'100vh', color:'white', display:'flex', flexDirection:'column', alignItems:'center', justifyContent:'center', fontFamily:'sans-serif'}}>
      <h1 style={{border:'2px solid #ffc107', padding:'10px 30px', borderRadius:'15px'}}>CHA<span style={{color:'#ffc107'}}>TOO</span></h1>
      {!user ? (
        <button onClick={login} style={{marginTop:'20px', padding:'15px 40px', backgroundColor:'#ffc107', color:'black', border:'none', borderRadius:'10px', fontWeight:'bold', cursor:'pointer'}}>دخول بواسطة Pi</button>
      ) : (
        <p>مرحباً {user.username}، أنت متصل الآن!</p>
      )}
    </div>
  );
}
