import React, { useState, useEffect } from 'react';

export default function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {

    const script = document.createElement('script');
    script.src = "https://sdk.minepi.com/pi-sdk.js";
    script.async = true;
    script.onload = () => {
      if (window.Pi) {
        window.Pi.init({ version: "2.0", sandbox: true });
      }
    };
    document.head.appendChild(script);
  }, []);

  const login = async () => {
    try {

      const auth = await window.Pi.authenticate(['username', 'payments'], (p) => {});
      setUser(auth.user);
    } catch (e) {
      alert("يجب فتح الرابط من داخل Pi Browser حصراً");
    }
  };

  const handlePay = async () => {
    try {

      await window.Pi.createPayment({
        amount: 0.1, 
        memo: "Chatoo Test Payment", 
        metadata: { id: Date.now() }
      }, {

        onReadyForServerApproval: (id) => fetch('https://lakes-carries-appropriate-gardens.trycloudflare.com/approve', { 
          method: 'POST', 
          headers: {'Content-Type': 'application/json'}, 
          body: JSON.stringify({ paymentId: id }) 
        }),
        onReadyForServerCompletion: (id, tx) => fetch('https://lakes-carries-appropriate-gardens.trycloudflare.com/complete', { 
          method: 'POST', 
          headers: {'Content-Type': 'application/json'}, 
          body: JSON.stringify({ paymentId: id, txid: tx }) 
        }),
        onCancel: () => console.log("تم إلغاء الدفع"),
        onError: (e) => alert("خطأ في الدفع: " + e.message)
      });
    } catch (e) {
      alert(e.message);
    }
  };

  return (
    <div style={{backgroundColor:'#0b141a', height:'100vh', color:'white', display:'flex', flexDirection:'column', alignItems:'center', justifyContent:'center', fontFamily:'sans-serif'}}>
      <h1 style={{border:'2px solid #ffc107', padding:'10px 30px', borderRadius:'15px'}}>CHA<span style={{color:'#ffc107'}}>TOO</span></h1>

      {!user ? (
        <button onClick={login} style={{marginTop:'20px', padding:'15px 40px', backgroundColor:'#ffc107', color:'black', border:'none', borderRadius:'10px', fontWeight:'bold', cursor:'pointer'}}>
          دخول بواسطة Pi
        </button>
      ) : (
        <div style={{textAlign:'center'}}>
          <p>أهلاً {user.username}</p>
          <button onClick={handlePay} style={{marginTop:'10px', padding:'15px 40px', backgroundColor:'#25d366', color:'white', border:'none', borderRadius:'10px', fontWeight:'bold', cursor:'pointer'}}>
            دفع (الخطوة 10)
          </button>
        </div>
      )}
    </div>
  );
}
